INSERT INTO `wp_posts` VALUES ('18', '1', '2013-12-08 09:20:04', '2013-12-08 01:20:04', '&lt;?php\r\n// Recent comments\r\n$max = 10; // item to get\r\nglobal $wpdb;\r\n$sql = \"SELECT c.*, p.post_title FROM $wpdb-&gt;comments c INNER JOIN $wpdb-&gt;posts p ON (c.comment_post_id=p.ID) WHERE comment_approved = \'1\' AND comment_type not in (\'trackback\',\'pingback\')\";\r\n$sql .= \" ORDER BY comment_date DESC LIMIT $max\";\r\n$results = $wpdb-&gt;get_results($sql);\r\n$template = \'&lt;div&gt;%g &lt;a href=\"%au\"&gt;%an&lt;/a&gt; 在《&lt;a href=\"%pu#comment-%cid\"&gt;%pt&lt;/a&gt;》上的评论：&lt;/div&gt;\';\r\n$echoed=0;\r\nforeach ($results as $row) {\r\n$tags = array(\'%ct\',\'%cd\',\'%g\',\'%pt\',\'%pu\',\'%au\',\'%an\',\'%cid\');\r\n$replacements = array($row-&gt;comment_title,$row-&gt;comment_date,get_avatar($row-&gt;comment_author_email,\'32\'),$row-&gt;post_title,get_permalink($row-&gt;comment_post_ID),$row-&gt;comment_author_url,$row-&gt;comment_author,$row-&gt;comment_ID);\r\necho \'&lt;li&gt;\' . str_replace($tags,$replacements,$template) . \'&lt;div&gt;\'. $row-&gt;comment_content . \'&lt;/div&gt;&lt;/li&gt;\';\r\n$echoed=1;\r\n}\r\nif ($echoed==0)\r\necho \'&lt;li&gt;找不到.&lt;/li&gt;\';\r\n?&gt;\r\n\r\n$tags = array(\'%ct\',\'%cd\',\'%g\',\'%pt\',\'%pu\',\'%au\',\'%an\',\'%cid\');\r\n\r\n说明：\r\n\r\n/*\r\n*    comment_title                                    =&gt; 评论标题\r\n*    comment_date                                     =&gt; 时间\r\n*    get_avatar($row-&gt;comment_author_email,\'32\')      =&gt; 根据评论者填写的邮箱获取avatar上的头像（32px）\r\n*    post_title                                         =&gt; 标题\r\n*    get_permalink($row-&gt;comment_post_ID)             =&gt; 文章链接\r\n*    comment_author_url                                =&gt; 评论者填写的作者主页\r\n*    comment_author                                     =&gt; 作者昵称\r\n*    comment_ID                                         =&gt; 评论ID\r\n*/\r\n\r\n&nbsp;', 'wordpress显示最新评论', '', 'publish', 'open', 'open', '', 'wordpress%e6%98%be%e7%a4%ba%e6%9c%80%e6%96%b0%e8%af%84%e8%ae%ba', '', '', '2013-12-11 11:27:56', '2013-12-11 03:27:56', '', '0', 'http://localhost/wordpress/?p=18', '0', 'post', '', '1');
